(582, 'A. autoturismul, autocamionul, motocicleta;', 'B. autocamionul, motocicleta, autoturismul;', 'C. autocamionul, autoturismul, motocicleta.', 'A'),
(583, 'A. albastru, roşu, verde;', 'B. albastru, verde, roşu;', 'C. roşu, albastru, verde.', 'B'),
