(589, 'A. autocamionul, tramvaiul, autoturismul;', 'B. autoturismul, autocamionul, tramvaiul;', 'C. tramvaiul, autoturismul, autocamionul.', 'A'),
(590, 'A. autocamionul, autoturismul verde, autoturismul roşu;', 'B. autoturismul roşu, autoturismul verde, autocamionul albastru;', 'C. autoturismul verde, autocamionul, autoturismul roşu.', 'B'),
